import React from 'react';
import { Form, FormGroup, Label, Input, Button } from 'reactstrap';


class Login extends Component() {

    state = {
        emai: "",
        password:  ""
    }

    onChange = (field, ev) =>{
        this.setState({[field]:ev.target.value})
    }

    render(){
        const {email} = this.state;
    return (

        <div className = "login card shadow text-center">
            
            <Form className = "form-signin text-center" style = {{padding:'50px', width: '550px'}}>
                <h1 class = "h3 mb-3 font-weight-normal">ÁreaRestrita</h1>

       
                    <FormGroup>
                <Label for="exampleEmail " >Usuário</Label>
                <Input 
                className = "mt-2"
                type="email" 
                value = {email}
                name="email" 
                id="email" 
                placeholder="Email do usuário" 
                onChange ={(ev) => this.onChangeInput("email", ev)}/>
                    </FormGroup> 

                            <FormGroup>
                <Label for="senha" className = "mt-2">Senha</Label>
                <Input className = "mt-2" type="password" name="senha" id="senha" placeholder="Senha" />
                            </FormGroup>

                            <Button className = "mt-4"style = {{width:'100%'}} color="primary btn-block">Acessar</Button>
                            <p className = "mt-2" > Esqueceu a senha?</p>
                            </Form>
        </div>
    )
    }
}

export default Login;
